import React, { useEffect, useState } from 'react'
import { useParams } from 'react-router-dom'
import { fetchSingleReceptit } from './data'

const API_KEY = "e509af0f80c74a1ea7d9794a7b97394c";
const API_SPOONACULAR = 'https://api.spoonacular.com/recipes'

// GET https://api.spoonacular.com/recipes/{id}/information

function Recipe() {
  const { id } = useParams()
  const [recipe, setRecipe] = useState({})

  useEffect(() => {
    // fetch(`${API_SPOONACULAR}/${id}/information?apiKey=${API_KEY}`)
    //   .then(res=>res.json())
    Promise.resolve(fetchSingleReceptit)
      .then(data => {
        console.log(data)
        setRecipe(data)
      })
  }, [id])
  // prendere id ricetta
  // chiamare api per id
  // mostrare la ricetta

  if (!recipe) {
    return (
      <div>Loading</div>
    )
  }
  return (
    <div>
      <h1>{recipe.title}</h1>
      {recipe.extendedIngredients.map((ingredient) => {
        return (
          <div>{ingredient.original}</div>
        )
      })}
    </div>
  )
}

export default Recipe
