import './App.css';
import { useEffect, useState } from 'react';
import { fetchDataApi } from './data'
import {Link} from "react-router-dom"

const API_KEY = "e509af0f80c74a1ea7d9794a7b97394c";
const API_SPOONACULAR = 'https://api.spoonacular.com/recipes'

function App() {
  const [ricette, setRicette] = useState([])
  const [ricerca, setRicerca] = useState("")
  const [ricercaInput, setRicercaInput] = useState("") 

  useEffect(() => {
    if (!ricerca) return

    // https://api.spoonacular.com/recipes/complexSearch?query=pasta&maxFat=25&number=2

    //fetch(`${API_SPOONACULAR}/complexSearch?apiKey=${API_KEY}&query=${ricerca}`)
      //.then(res => res.json())
      Promise.resolve(fetchDataApi)
      .then(data => data.results)
      .then(results => {
        console.log(results)
        setRicette(results)
      })
  }, [ricerca])

  return (
    <div className="container mx-auto">

      <h1 className='text-3xl text-center' > Ricette vegetariane </h1>
      <div>
        <form onSubmit={(evt)=>{
          evt.preventDefault()
          setRicerca(ricercaInput)
        }}>
          <input type="text" onChange={(evt)=>{
            setRicercaInput(evt.target.value)
          }} />
          <button>Cerca ricetta</button>
        </form>
      </div>

      <div>
        Risultati
        {ricette.map((ricetta) => {
          return (
            <div>
              <h2><Link to={`/recipe/${ricetta.id}`}>{ricetta.title}</Link></h2>
              <img src={ricetta.image}/>
            </div>
          )
        })}
      </div>
    </div>
  );
}

export default App;
