    return <input type="text"
        className="col-span-3 mt-1 px-3 py-2 bg-white border shadow-sm border-slate-300 placeholder-slate-400 focus:outline-none focus:border-sky-500 focus:ring-sky-500 block w-full rounded-md sm:text-sm focus:ring-1"
        placeholder='Type a vegetarian ingredient...'
        onChange={(e) => setValue(e.target.value)}
    />
}


