import './App.css';
import { useEffect, useState } from 'react';
import { fetchDataApi } from './data'

// const API_KEY = "e509af0f80c74a1ea7d9794a7b97394c";

function App() {
  const [ricette, setRicette] = useState([])
  const [ricerca, setRicerca] = useState("")
  const [ricercaInput, setRicercaInput] = useState("") 

  useEffect(() => {
    console.log('chiamo ricerca')
    // fetch(`https://api.spoonacular.com/recipes/complexSearch?apiKey=${API_KEY}`)
    // .then(res => res.json())
    Promise.resolve(fetchDataApi)
      .then(data => data.results)
      .then(results => {
        console.log(results)
        setRicette(results)
      })
  }, [ricerca])

  return (
    <div className="App">

      <div> Ricette vegetariane </div>
      <div>
        <form onSubmit={(evt)=>{
          evt.preventDefault()
          setRicerca(ricercaInput)
        }}>
          <input type="text" onChange={(evt)=>{
            setRicercaInput(evt.target.value)
          }} />
          <button>Cerca ricetta</button>
        </form>
      </div>

      <div>
        Risultati
        {ricette.map((ricetta) => {
          return (
            <div>
              <h2>{ricetta.title}</h2>
              <img src={ricetta.image}/>
            </div>
          )
        })}
      </div>
    </div>
  );
}

export default App;
