import './App.css';
import { useEffect, useState } from 'react';
import { fetchDataApi } from './data'
import { Link } from "react-router-dom"
import Input from './components/Input';

const API_KEY = "e509af0f80c74a1ea7d9794a7b97394c";
const API_SPOONACULAR = 'https://api.spoonacular.com/recipes'

function App() {
  const [ricette, setRicette] = useState([])
  const [ricerca, setRicerca] = useState("")
  const [ricercaInput, setRicercaInput] = useState("")

  useEffect(() => {
    if (!ricerca) return

    // https://api.spoonacular.com/recipes/complexSearch?query=pasta&maxFat=25&number=2

    //fetch(`${API_SPOONACULAR}/complexSearch?apiKey=${API_KEY}&query=${ricerca}`)
    //.then(res => res.json())
    // Promise.resolve(fetchDataApi)
    //   .then(data => data.results)
    //   .then(results => {
    //     console.log(results)
    //     setRicette(results)
    //   })
  }, [ricerca])

  return (
    <div className="container mx-auto">

      <h1 className='text-4xl text-center pt-4 pb-4' > Ricette vegetariane </h1>
      <div>
        <form onSubmit={(evt) => {
          evt.preventDefault()
          setRicerca(ricercaInput)
        }}>
          <div className='content-center grid grid-cols-4 gap-3'>
            <Input/>
            <button className='bg-sky-500 hover:bg-sky-700 rounded-md text-white'>Cerca ricetta</button>
          </div>
        </form>
      </div>

      <div className='pt-4'>
        <div className='grid grid-cols-3 gap-3'>
          {ricette.map((ricetta) => {
            return (
              <div className='border border-slate-200 rounded-md'>
                <div className='m-4 rounded-md overflow-hidden'><img  src={ricetta.image} /></div>


                <h2 className='px-4 pb-4'><Link to={`/recipe/${ricetta.id}`}>{ricetta.title}</Link></h2>
              </div>
            )
          })}
        </div>
      </div>
    </div>
  );
}

export default App;
