import {createUseStyles} from 'react-jss'
import { useState } from 'react';
import { Link } from "react-router-dom";
import axios from 'axios';
import Input from './components/Input';

const API_KEY = "e509af0f80c74a1ea7d9794a7b97394c";
const API_SPOONACULAR = 'https://api.spoonacular.com/recipes'

const useStyles = createUseStyles({
  myButton: {
    color: 'green',
    margin: {
      // jss-plugin-expand gives more readable syntax
      top: 5, // jss-plugin-default-unit makes this 5px
      right: 0,
      bottom: 0,
      left: '1rem'
    },
    '& span': {
      // jss-plugin-nested applies this to a child span
      fontWeight: 'bold' // jss-plugin-camel-case turns this into 'font-weight'
    }
  },
})


function App() {
  const [ricette, setRicette] = useState([])
  const [ricerca, setRicerca] = useState("bean")
  const [ricercaInput, setRicercaInput] = useState("")

  // useEffect(()=>{ 
  //     searchRecipe();
  // },[])

  const searchRecipe = async () => { 
     try {
      const res = await axios.get(`${API_SPOONACULAR}/complexSearch?apiKey=${API_KEY}&query=${ricerca}&diet=vegetarian`);
      setRicette(res.data.results);
     } catch (e) {
       console.log(e);
     }
  }

  return (
    <div className="container mx-auto">
      <h1 className='text-4xl text-center pt-4 pb-4' > Ricette vegetariane </h1>
      <div>
          <div className='content-center grid grid-cols-4 gap-3'>
            <Input setValue={setRicerca}/>
            <button onClick={()=>searchRecipe()}className='bg-sky-500 hover:bg-sky-700 rounded-md text-white'>Cerca ricetta</button>
          </div>
      </div>

      <div className='pt-4'>
        <div className='grid grid-cols-3 gap-3'>
          {ricette.map((ricetta) => {
            return (
              <div className='border border-slate-200 rounded-md'>
                <div className='m-4 rounded-md overflow-hidden'><img  src={ricetta.image} /></div>


                <h2 className='px-4 pb-4'><Link to={`/recipe/${ricetta.id}`}>{ricetta.title}</Link></h2>
              </div>
            )
          })}
        </div>
      </div>
    </div>
  );
}

export default App;
