import {createUseStyles} from 'react-jss'
import { useState } from 'react';
import { Link } from "react-router-dom";
import axios from 'axios';
import Input from './components/Input';

const API_KEY = "e509af0f80c74a1ea7d9794a7b97394c";
const API_SPOONACULAR = 'https://api.spoonacular.com/recipes'

const useStyles = createUseStyles({
  recipes: {
    display:'flex',
    justifyContent:"space-between",
    padding:'16px',
    flexWrap:'wrap',
    gap:'32px',
    alignItems:'center'
  },
  card:{ 
    maxWidth:300,
    backgroundColor:'#333',
    padding:16
  },
  recipeImage : { 
    width:'300px',
    height:'200px',
    backgroundSize:'cover'
  },
  link:{
    textDecoration:'none',
    color:'white',
    '&:hover':{
      color:'blue'
    }
  }
})


function App() {
  const classes = useStyles()
  const [ricette, setRicette] = useState([])
  const [ricerca, setRicerca] = useState("bean")
  const [ricercaInput, setRicercaInput] = useState("")

  // useEffect(()=>{ 
  //     searchRecipe();
  // },[])

  const searchRecipe = async () => { 
     try {
      const res = await axios.get(`${API_SPOONACULAR}/complexSearch?apiKey=${API_KEY}&query=${ricerca}&diet=vegetarian`);
      setRicette(res.data.results);
     } catch (e) {
       console.log(e);
     }
  }

  return (
    <div className={classes.container}>
      <h1 className='text-4xl text-center pt-4 pb-4' > Ricette vegetariane </h1>
      <div>
          <div className='content-center grid grid-cols-4 gap-3'>
            <Input setValue={setRicerca}/>
            <button onClick={()=>searchRecipe()}className='bg-sky-500 hover:bg-sky-700 rounded-md text-white'>Cerca ricetta</button>
          </div>
      </div>

        <div className={classes.recipes}>
          {ricette.map((ricetta) => {
            return (
              <div className={classes.card}>
                <div  className={classes.recipeImage} style={{backgroundImage:`url("${ricetta.image}"`}} />
                <h2><Link className={classes.link} to={`/recipe/${ricetta.id}`}>{ricetta.title}</Link></h2>
              </div>
            )
          })}
        </div>
      </div>
  );
}

export default App;
