import {createUseStyles} from 'react-jss'
import "./App.css";
import { useState } from 'react';
import { Link } from "react-router-dom";
import axios from 'axios';
import Input from './components/Input';

const API_SPOONACULAR = 'https://api.spoonacular.com/recipes'

const useStyles = createUseStyles({
  recipes: {
    display:'flex',
    justifyContent:"space-between",
    padding:'16px',
    flexWrap:'wrap',
    gap:'32px',
    alignItems:'center'
  },
  card:{ 
    maxWidth:300,
    backgroundColor:'#275610',
    padding:16,
    borderRadius:8,
    marginTop:'60px',
    fontFamily:'Rubik',
  },
  recipeImage : { 
    width:'300px',
    height:'200px',
    backgroundSize:'cover'
  },
  link:{
    textDecoration:'none',
    color:'white',
    '&:hover':{
      color:'lightgreen'
    }
  },

})


function App() {
  const classes = useStyles()
  const [recipes, setRecipes] = useState([])
  const [ricerca, setRicerca] = useState("bean")
  // const [ricercaInput, setRicercaInput] = useState("")


  const searchRecipe = async () => { 
     try {
      const res = await axios.get(`${API_SPOONACULAR}/complexSearch?apiKey=${process.env.REACT_APP_API_KEY}&query=${ricerca}&diet=vegetarian`);
      setRecipes(res.data.results);
     } catch (e) {
       console.log(e);
     }
  }

  return (
    <div className={classes.container}>
      <h1 className='title'> Welcome to Damiano's Vegetarian Site </h1>
      <div>
          <div className='searchContainer'>
            <Input setValue={setRicerca}/>
            <button className="searchButton" onClick={()=>searchRecipe()}>Search a recipe!</button>
          </div>
      </div>
        <div className={classes.recipes}>
          {recipes.map((recipe) => {
            return (
              <div className={classes.card}>
                <div  className={classes.recipeImage} style={{backgroundImage:`url("${recipe.image}"`}} />
                <h2><Link className={classes.link} to={`/recipe/${recipe.id}`}>{recipe.title}</Link></h2>
              </div>
            )
          })}
        </div>
      </div>
  );
}

export default App;
