import React, { useEffect, useState } from 'react'
import { useParams } from 'react-router-dom'
import { fetchSingleReceptit } from './data'
import { Link } from "react-router-dom"



const API_KEY = "e509af0f80c74a1ea7d9794a7b97394c";
const API_SPOONACULAR = 'https://api.spoonacular.com/recipes'

// GET https://api.spoonacular.com/recipes/{id}/information

function Recipe() {
  const { id } = useParams()
  const [recipe, setRecipe] = useState(null)

  useEffect(() => {
    // fetch(`${API_SPOONACULAR}/${id}/information?apiKey=${API_KEY}`)
    //   .then(res=>res.json())
    Promise.resolve(fetchSingleReceptit)
      .then(data => {
        console.log(data)
        setRecipe(data)
      })
  }, [id])
  // prendere id ricetta
  // chiamare api per id
  // mostrare la ricetta

  if (!recipe) {
    return (
      <div>Loading</div>
    )
  }

  return (
    <div className='container'>
      <h1 className='text-4xl text-center pt-4 pb-4'>{recipe.title}</h1>
      <div className='grid grid-cols-2 gap-6'>
        <div className='rounded-md overflow-hidden'><img src={recipe.image} /></div>
        <div>
          {recipe.extendedIngredients.map((ingredient) => {
            return (
              <div>{ingredient.original}</div>
            )
          })}
        </div>
      </div>
      <div className='bg-sky-500 hover:bg-sky-100 rounded-md text-white'> 
      <Link to={`/`}>Indietro</Link> 
      </div>
    </div>

  )
}

export default Recipe
